import React, { useState } from "react";


function App() {

  const [headingText, setHeadingText] = useState("hello"); // this is a state variable
  const [isMousedOver, setMouseOver]= useState(false);

  function handleClick(){

   setHeadingText("kakied"); // this is setting the state variable
  }

  function mouseOver(){
    setMouseOver(true);
  }

  function mouseOff(){
    if (setMouseOver === true){
      setMouseOver = false;
    }
  }

  return (
    <div className="container">
      <h1>{headingText}</h1>
      <input type="text" placeholder="What's your name?" />
      <button 
       style={{backgroundColor: isMousedOver ? "black" : "white"}}
        onClick={handleClick}
        onMouseOver={mouseOver}
        onMouseOut={mouseOff}


      >Submit</button>
    </div>
  );
}

export default App;
